Vue.component('is-autogenerated-filter', {
    data() {
        return {
            name: 'is_autogenerated',
            LANG_MESSAGES: LANG_MESSAGES,
            is_autogenerated: 0
        };
    },
    mixins: [filters_mixin],

    mounted() {
        this.is_autogenerated = getUrlVars()['is_autogenerated'] || 0;
        filters_bus.$emit(this.name + '-init', parseInt(this.is_autogenerated));
    },

    watch: {
        'is_autogenerated'() {
            filters_bus.$emit(this.name + '-updated', parseInt(this.is_autogenerated));
        }
    },

    template: `<div class="filter">
         <div class="checkbox checkbox-success">
            <input v-model="is_autogenerated" :true-value="1" :false-value="0" 
            type="checkbox" id="is_autogenerated">
            <label for="is_autogenerated">{{ LANG_MESSAGES.is_autogenerated }}</label>
        </div>
    </div>`
});


